import React, { useState } from 'react'
import ReactDOM from 'react-dom'

const Statistics = (props) => {
  let total = props.good + props.neutral + props.bad
  if (total == 0) {
    return (
      <div>
        <h1>
          <p>{'statistics'}</p>
        </h1>
        <p>{'no feedback given'}</p>
      </div>
    )
  }
  return (
    <div>
      <h1>
        <p>{'statistics'}</p>
      </h1>
      <p>{'good '} {props.good}</p>
      <p>{'neutral'}  {props.neutral}</p>
      <p>{'bad '} {props.bad}</p>
      <p>{'total '} {(total)}</p>
      <p>{'average '} {(props.good - props.bad)/(total)}</p>
      <p>{'positive '} {100  *  (props.good /(total))} {'%'}</p>
    </div>
  )
}
const Button = (props) => (
  <button onClick={props.handleClick}>
    {props.text}
  </button>
)

const App = () => {
  const [value, setValue] = useState(0)

  const setToValue = newValue => {
    setValue(newValue)
  }

  /* tallenna napit omaan tilaansa
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0) */

  return (
    <div>
        <div>
          <h1>
            <p>{'give feedback'}</p>
          </h1>
            <Button handleClick={() => setToValue(value + 1)} text="good" />
            <Button handleClick={() => setToValue(value + 1)} text="neutral" />
            <Button handleClick={() => setToValue(value + 1)} text="bad" />

            <div>
              <Statistics good={good.value} neutral={neutral.value} bad={bad.value} />
            </div>
        </div>

    </div>
  )
}

ReactDOM.render(<App />, 
  document.getElementById('root')
)
